mapper.update:
update USER
set userNm = #{userNm}, birthday = #{birthday}, phoneNumber = #{phoneNumber}, email = #{email}
where userId = #{userId}

update USER
set password = #{password} , salt = #{salt}
where userId = #{userId}

update USER
set userState = "9"
where userId = #{userId} and password = #{password}

update USER
set userState = #{userState}
where userId = #{userId}
